import { DetailBox, DetailContainer, DetailContent, Detailed, ImgDetails, XButtons, } from "./Guns.styles";

import styled from "styled-components";

import BP50 from "../../assets/images/BP50.jpg";
import XButton from "../../assets/icons/remove.png";
import { useEffect, useState } from "react";


import Axios from "axios";

type GunsProps =
{
    callFunction: () => void;
    gunNames: string;
}

interface GunsData {
    gunName: string;
}

export function GunsDetails({ callFunction, gunNames}: GunsProps){
    const [dados, setDados] = useState<GunsData[]>([]);
    const [data, setData] = useState<GunsData | null>(null);
    
    

    


    useEffect(() =>{
            const fetchData = async () =>{
            const response = await Axios.get("http://localhost:8080/");
            setDados(response.data);
            }
            fetchData();
    }, []);


    useEffect(() =>{
        dados.map((item, index) =>{
            if(item.gunName === gunNames){
                setData(item);
            }
        });
    }, [dados, gunNames]);


    function handleVisibility(){
        callFunction();
    }

    return(
        <DetailContainer>
            <DetailBox>
                <XButtons onClick={() => handleVisibility()} src={XButton}></XButtons>
                <h1>{data && data.gunName}</h1>
            <ImgDetails src="https://imagedelivery.net/BN5t48p9frV5wW3Jpe6Ujw/dg-58-lsw-wzstats-48f81e/gunFullDisplay"></ImgDetails>
                <DetailContent>
                    <Detailed>
                        <p>Cano Pesado Lore-9</p>
                        <span>Cano</span>
                    </Detailed>

                    <Detailed>
                        <p>Silenciador VT-7 SP</p>
                        <span>Boca</span>
                    </Detailed>

                    <Detailed>
                        <p>Projéteis OTAN 5.56</p>
                        <span>Munição</span>
                    </Detailed>

                    <Detailed>
                        <p>Carregador de 45 Projéteis</p>
                        <span>Carregador</span>
                    </Detailed>

                    <Detailed>
                        <p>Fita Aderente TRST</p>
                        <span>Cabo</span>
                    </Detailed>
                </DetailContent>
            </DetailBox>
                
        </DetailContainer>
    )
}
